ValidationRule.errorConditionFormula:
OR(
	OR(
		AND(
			NOT(ISBLANK(PersonHomePhone)), 
			NOT(REGEX(PersonHomePhone,"^(\\+0?1\\s)?\\(?\\d{3}\\)?[\\s.-]?\\d{3}[\\s.-]?\\d{4}$"))
			), 
		AND(
			NOT(ISBLANK(Work_Phone__c)), 
			NOT(REGEX(Work_Phone__c,"^(\\+0?1\\s)?\\(?\\d{3}\\)?[\\s.-]?\\d{3}[\\s.-]?\\d{4}$"))
			)
		), 
	AND(
		NOT(ISBLANK(PersonMobilePhone)), 
		NOT(REGEX(PersonMobilePhone,"^(\\+0?1\\s)?\\(?\\d{3}\\)?[\\s.-]?\\d{3}[\\s.-]?\\d{4}$"))
		)
	)
